'use strict';

const hbs = require('express-hbs');
const dropdownStyles = require('./styles/dropdown');

hbs.registerHelper('gh-dd-container', function () {
    let cssClass = dropdownStyles.container();
    return new hbs.handlebars.SafeString(cssClass);
});

hbs.registerHelper('gh-dd-content', function (size, dropup) {
    let cssClass = dropdownStyles.content(size, dropup);
    return new hbs.handlebars.SafeString(cssClass);
});
