'use strict';

const hbs = require('express-hbs');
const formStyles = require('./styles/form');

hbs.registerHelper('gh-form', function (element, options) {
    let cssClass = formStyles(element, {
        status: options.hash.status,
        icon: options.hash.icon,
        coloring: options.hash.coloring
    });

    return new hbs.handlebars.SafeString(cssClass);
});
